  constructor(props) {
    super(props)
    this.state ={
      stuff: ['code', 'sleep'],
      newItem: ""
    }
    this.addToStuff = this.addToStuff.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  addToStuff(item) {
    this.setState({
      stuff: this.state.stuff.concat([item])
    })
  }

  handleChange(event) {
    this.setState({
      newItem: event.target.value
    })
  }
  render() {
    return (
