import React, { Component } from "react";
import "./App.css";
import List from "./components/List"
import Todobutton from "./components/Todobutton"

class App extends Component {
  constructor(props) {
    super(props)
    this.state ={
      stuff: ['code', 'sleep'],
      newItem: ""
    }
    this.addToStuff = this.addToStuff.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }

  addToStuff(item) {
    this.setState({
      stuff: this.state.stuff.concat([item])
    })
  }

  handleChange(event) {
    this.setState({
      newItem: event.target.value
    })
  }
  render() {
    return (
      <div className="App">


        <input value={this.state.newItem} onChange={this.handleChange} />
        <Todobutton action={this.addToStuff.bind(this, this.state.newItem)}>
         add stuff
        </Todobutton>


        <List title="stuff to do" list={this.state.stuff} />
        <div className="name">
            <h1>{props.name}</h1>
            <div className="fromjobtitle">
            <ul>
                <h4>
                 From:   {props.city}
                </h4>
                <h4>
                 Job Title: {props.title}
                </h4>
                <h4>
                    Employer: {props.employer}
                </h4>
            </ul>
            </div>
            <div className="movies">
                Favorite Movies:
                <ul>
                    {props.favoriteMovies}
                </ul>
            </div>
        </div>




      </div>
    );
  }
}

export default App;
